
//<button type="button" class="btn btn-warning" data-toggle="modal" data-target="#siteModal_editar"> Editar </button>"+

$(function(){
	$(document).on("click", "#btnAdicionar", function() {	    
        let cols = "";	
        let name = $("#exampleInputName").val()
        let email = $("#exampleInputEmail1").val()
        let disc = $("#exampleInputdisciplica1").val()
        let period = $("#exampleInputperiodo1").val()
        cols += "<tr>"
        cols += '<td>' + name + '</td>';
		cols += '<td>' + email + '</td>';	    
		cols += '<td>' + disc + '</td>';	    
		cols += '<td>' + period + '</td>';		    
		cols += '<td>';	    
		cols += '<button type="button" class="btn btn-warning" data-toggle="modal" data-target="#siteModal_editar" id="btnExcluir">Remover</button>';
		cols += '<button id="siteModal_editar" type="button">Editar</button>';	      
        cols += '</td>';	
		cols += "</tr>"
		$("table tbody").append(cols);
		console.log($("#tableCadastro tbody"))
	})
		

	function Salvar(){
		var par = $(this).parent().parent(); //tr
		var tdNome = par.children("td:nth-child(1)");
		var tdTelefone = par.children("td:nth-child(2)");
		var tdEmail = par.children("td:nth-child(3)");
		var tdBotoes = par.children("td:nth-child(4)");

		tdNome.html(tdNome.children("input[type=text]").val());
		tdTelefone.html(tdTelefone.children("input[type=text]").val());
		tdEmail.html(tdEmail.children("input[type=text]").val());
		tdBotoes.html("<img src='images/delete.png'class='btnExcluir'/><img src='images/pencil.png' class='btnEditar'/>");

		$(".btnEditar").bind("click", Editar);
		$(".btnExcluir").bind("click", Excluir);
	};

	$(document).on("click", "#btnEditar", function(){
		var par = $(this).parent().parent(); //tr
		var tdNome = par.children("td:nth-child(1)");
		var tdTelefone = par.children("td:nth-child(2)");
		var tdEmail = par.children("td:nth-child(3)");
		var tdBotoes = par.children("td:nth-child(4)");

		tdNome.html("<input type='text' id='txtNome' value='"+tdNome.html()+"'/>");
		tdTelefone.html("<input type='text' id='txtTelefone' value='"+tdTelefone.html()+"'/>");
		tdEmail.html("<input type='text' id='txtEmail' value='"+tdEmail.html()+"'/>");
		tdBotoes.html("<img src='images/disk.png' class='btnSalvar'/>");

		$(".btnSalvar").bind("click", Salvar);
		$(".btnEditar").bind("click", Editar);
		$(".btnExcluir").bind("click", Excluir);
	});


	function Excluir(){
	    var par = $(this).parent().parent(); //tr
	    par.remove();
	};

	$("#btnEditar").bind("click", Editar);
	$(".btnExcluir").bind("click", Excluir);
	$("#btnAdicionar").bind("click", Adicionar); 
});
